 ) {
 Icon(Icons.Default.Add, contentDescription = "Add Expense")
 }
 }
 }
 ) { paddingValues ->
 if (group == null) {
 // Loading state
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues),
 contentAlignment = Alignment.Center
 ) {
 CircularProgressIndicator()
 }
 } else {
 // Group details content
