+@Composable
+fun QRCodeScanner(onQrCodeDetected: (String) -> Unit) {
+ val context = LocalContext.current
+ val lifecycleOwner = LocalLifecycleOwner.current
+ 
+ // Executor for camera operations
+ val executor = remember { Executors.newSingleThreadExecutor() }
+ 
+ // Set up preview view
+ val previewView = remember {
+ PreviewView(context).apply {
+ scaleType = PreviewView.ScaleType.FILL_CENTER
+ layoutParams = ViewGroup.LayoutParams(
+ ViewGroup.LayoutParams.MATCH_PARENT,
+ ViewGroup.LayoutParams.MATCH_PARENT
+ )
+ }
+ }
+ 
+ // Create barcode scanner
+ val barcodeScanner = remember { BarcodeScanning.getClient() }
+ 
+ // Flag to prevent multiple detections
+ var isScanning by remember { mutableStateOf(true) }
+ 
+ // Clean up resources when the composable leaves composition
+ DisposableEffect(lifecycleOwner) {
+ onDispose {
+ executor.shutdown()
+ barcodeScanner.close()
+ }
+ }
+ 
+ AndroidView(
+ factory = { previewView },
+ modifier = Modifier.fillMaxSize()
+ ) { view ->
+ val cameraProviderFuture = ProcessCameraProvider.getInstance(context)
+ 
+ cameraProviderFuture.addListener({
+ val cameraProvider = cameraProviderFuture.get()
+ 
+ // Set up preview use case
+ val preview = Preview.Builder().build().also {
+ it.setSurfaceProvider(view.surfaceProvider)
+ }
+ 
+ // Set up image analysis use case
+ val imageAnalysis = ImageAnalysis.Builder()
+ .setBackpressureStrategy(ImageAnalysis.STRATEGY_KEEP_ONLY_LATEST)
+ .build()
+ .apply {
+ setAnalyzer(executor) { imageProxy ->
+ if (isScanning) {
+ val mediaImage = imageProxy.image
+ if (mediaImage != null) {
+ val image = InputImage.fromMediaImage(
+ mediaImage, 
+ imageProxy.imageInfo.rotationDegrees
+ )
+ 
+ barcodeScanner.process(image)
+ .addOnSuccessListener { barcodes ->
+ for (barcode in barcodes) {
+ barcode.rawValue?.let { value ->
+ // Successfully detected a QR code
+ Log.d("QRScanner", "Detected QR code: $value")
+ 
+ // Set scanning to false to prevent multiple detections
+ isScanning = false
+ 
+ // Pass the result back to the caller
+ onQrCodeDetected(value)
+ }
+ }
+ }
+ .addOnCompleteListener {
+ imageProxy.close()
+ }
+ } else {
+ imageProxy.close()
+ }
+ } else {
+ imageProxy.close()
+ }
+ }
+ }
+ 
+ try {
+ // Unbind all use cases before rebinding
+ cameraProvider.unbindAll()
+ 
+ // Bind use cases to camera
+ cameraProvider.bindToLifecycle(
+ lifecycleOwner,
+ CameraSelector.DEFAULT_BACK_CAMERA,
+ preview,
+ imageAnalysis
+ )
+ } catch (e: Exception) {
+ Log.e("QRScanner", "Use case binding failed", e)
+ }
+ }, ContextCompat.getMainExecutor(context))
+ }
