+package com.example.bestsplit

+import android.Manifest
+import android.content.Intent
+import android.content.pm.PackageManager
+import android.os.Bundle
+import android.util.Log
+import android.widget.Toast
+import androidx.activity.ComponentActivity
+import androidx.core.app.ActivityCompat
+import androidx.core.content.ContextCompat
+
+class QrScannerActivity : ComponentActivity() {
+ private lateinit var codeScanner: CodeScanner
+ private val CAMERA_PERMISSION_REQUEST_CODE = 100
+
+ override fun onCreate(savedInstanceState: Bundle?) {
+ super.onCreate(savedInstanceState)
+ setContentView(R.layout.activity_qr_scanner)
+
+ // Check for camera permission
+ if (ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA)
+ != PackageManager.PERMISSION_GRANTED
+ ) {
+ ActivityCompat.requestPermissions(
+ this, arrayOf(Manifest.permission.CAMERA),
+ CAMERA_PERMISSION_REQUEST_CODE
+ )
+ } else {
+ setupScanner()
+ }
+ }
+
+ private fun setupScanner() {
+ val scannerView = findViewById<com.budiyev.android.codescanner.CodeScannerView>(R.id.scanner_view)
+ 
+ codeScanner = CodeScanner(this, scannerView).apply {
+ camera = CodeScanner.CAMERA_BACK
+ formats = CodeScanner.ALL_FORMATS
+ autoFocusMode = AutoFocusMode.SAFE
+ scanMode = ScanMode.SINGLE
+ isAutoFocusEnabled = true
+ isFlashEnabled = false
+
+ // Set decode callback
+ decodeCallback = DecodeCallback { result ->
+ runOnUiThread {
+ Log.d("QrScanner", "Scan result: ${result.text}")
+ 
+ // Return the result to the calling activity
+ val resultIntent = Intent().apply {
+ putExtra("SCAN_RESULT", result.text)
+ }
+ setResult(RESULT_OK, resultIntent)
+ finish()
+ }
+ }
+
+ // Set error callback
+ errorCallback = ErrorCallback { error ->
+ runOnUiThread {
+ Log.e("QrScanner", "Scan error: ${error.message}", error)
+ Toast.makeText(
+ this@QrScannerActivity,
+ "Scanner error: ${error.message}",
+ Toast.LENGTH_SHORT
+ ).show()
+ }
+ }
+ }
+ }
+
