 val amountValue = amount.toDoubleOrNull() ?: 0.0
 viewModel.addSettlement(
 groupId = groupId,
 fromUserId = members[selectedFromUserIndex].id,
 toUserId = members[selectedToUserIndex].id,
 amount = amountValue,
