+@Composable
+fun QRScanner(
+ context: Context,
+ lifecycleOwner: androidx.lifecycle.LifecycleOwner,
+ cameraExecutor: ExecutorService,
+ onQrCodeDetected: (String) -> Unit
+) {
+ val previewView = remember { PreviewView(context) }
+ val qrAnalyzer = remember { QrCodeAnalyzer { qrCode -> 
