 viewModelScope.launch {
 val user = repository.signIn(email, password)
 if (user != null) {
 onSuccess()
 } else {
 onError("Sign in failed")
 }
 }
 }

 fun signOut() {
 repository.signOut()
 }

 // Configure Google Sign In
