+ fun handleSignInResult(
+ data: Intent?,
+ onSuccess: (GoogleSignInAccount) -> Unit,
+ onError: (String) -> Unit
+ ) {
+ try {
+ val task = GoogleSignIn.getSignedInAccountFromIntent(data)
+ val account = task.getResult(ApiException::class.java)
+
+ if (account?.idToken != null) {
+ Log.d(TAG, "Google account successfully retrieved: ${account.email}")
+ onSuccess(account)
+ } else {
+ Log.e(TAG, "Google account has no ID token")
+ onError("Invalid sign-in (no token)")
+ }
+ } catch (e: ApiException) {
+ Log.e(TAG, "Google sign in failed: code=${e.statusCode}", e)
+ onError("Sign-in error (${e.statusCode}): ${e.localizedMessage ?: "Unknown error"}")
+ } catch (e: Exception) {
+ Log.e(TAG, "Unexpected error in sign-in", e)
+ onError("Sign-in error: ${e.localizedMessage ?: "Unknown error"}")
+ }
+ }
+
+ /**
+ * Sign in with Firebase using Google credentials
+ */
+ fun firebaseAuthWithGoogle(
+ account: GoogleSignInAccount,
+ onSuccess: () -> Unit,
+ onError: (String) -> Unit
+ ) {
+ Log.d(TAG, "Starting Firebase auth with Google")
+ val credential = GoogleAuthProvider.getCredential(account.idToken, null)
+
+ FirebaseAuth.getInstance().signInWithCredential(credential)
+ .addOnSuccessListener {
+ Log.d(TAG, "Firebase auth successful: ${it.user?.uid}")
+ onSuccess()
+ }
+ .addOnFailureListener { e ->
+ Log.e(TAG, "Firebase auth failed", e)
+ onError(e.localizedMessage ?: "Authentication failed")
+ }
+ }
+
+ companion object {
+ private const val TAG = "GoogleSignInHelper"
+
+ @Volatile
+ private var INSTANCE: GoogleSignInHelper? = null
+
+ fun getInstance(context: Context): GoogleSignInHelper {
+ return INSTANCE ?: synchronized(this) {
+ val instance = GoogleSignInHelper(context)
+ INSTANCE = instance
+ instance
+ }
+ }
+ }
